import React, { Component, useState, useMemo, useEffect } from 'react';
import axios from 'axios';
//import Table from ".Components/Table";
import './App.css';
import 'react-table/react-table.css';

class App extends Component() {

  constructor(props) {
    super(props);
    this.state = {
      posts: []
    }
  }

  componentDidMount() {
    const url = 'https://jsonplaceholder.typicode.com/users';
    fetch(url, {
      method: 'GET'
    }).then(response => response.json())
      .then(posts => {
       this.setState({posts: posts})
      })
  }

  render() {
    const columns = [
      {
        Header: 'Name',
        accessor: 'name',
        style: {
          textAlign: 'right',
          width: 100,
          minWidth: 100,
          maxWidth: 100
        }
      },
      {
        Header: 'Email',
        accessor: 'email',
        style: {
          textAlign: 'right',
          width: 100,
          minWidth: 100,
          maxWidth: 100
        }
      },
      {
        Header: 'Phone',
        accessor: 'phone',
        sortable: false,
        filterable: false
      },
      {
        Header: 'Website',
        accessor: 'website',
        sortable: false,
        filterable: false
      }
    ]

    return (
      <ReactTable
        columns = {columns}
        data = {this.state.posts}
        filterable
        defaultPageSize = {5}
      >
      </ReactTable>
    )
  }
}
export default App;

